/**
 * Icons
 */

/**
 * Get svg icon
 */
function hms_get_icon( $name ) {
	if ( ! $name ) {
		return false;
	}

	$icons = hms_get_icons();

	if ( ! isset( $icons[ $name ] ) ) {
		return false;
	}

	$icon = $icons[ $name ];

	return $icon;
}

/**
 * Display svg icon
 */
function hms_icon( $name ) {
	if ( ! $name ) {
		return;
	}

	$icon = hms_get_icon( $name );

	echo wp_kses( $icon, 'content' );
}

/**
 * Get admin menu svg icon
 */
function hms_get_menu_icon( $base64 = true ) {
	$icon = hms_get_icon( 'hms' );

	if ( $base64 ) {
		//phpcs:ignore
		return 'data:image/svg+xml;base64,' . base64_encode( $icon );
	}

	return $icon;
}


